INSERT INTO `doc_document` (`id`, `title`, `titleonly`, `test`, `startscreen`, `content`, `content_hidden`, `url`, `enable`, `preid`, `sort`) VALUES
(169, '抽样单javabean字段说明', 0, 0, 0, '<p>\n SampleFormNumber;//抽样单号\n</p>\n<p>\n OnLine;//在线抽样单号还是离线抽样单号\n</p>\n<p>\n pharmaceuticalInforamation;//药品信息\n</p>\n<ul>\n <li>\n  <span>registerNumber;//注册证号</span> \n </li>\n <li>\n  <span>compactNumber;//合同号</span> \n </li>\n <li>\n  <span>checkInformNumber;//检验通知单号</span> \n </li>\n <li>\n  <span>chineseName;//中文名称</span> \n </li>\n <li>\n  <span>englishName;//英文名称</span> \n </li>\n <li>\n  <span>pharmaceuticalName;//商品名称</span> \n </li>\n <li>\n  <span>doseModel;//剂型</span> \n </li>\n <li>\n  <span>specification;//规格</span> \n </li>\n <li>\n  <span>validityPeriod;//效期</span> \n </li>\n <li>\n  <span>lotNumber;//批号</span> \n </li>\n <li>\n  <span>storeConditionl;//贮藏条件</span> \n </li>\n <li>\n  <span>productRegion;//产地</span> \n </li>\n <li>\n  <span>productDepartment;//生产单位</span> \n </li>\n</ul>\n<p>\n sampleSpot;//抽样现场\n</p>\n<ul>\n <li>\n  <span>storePlace;//存货地点</span> \n </li>\n <li>\n  <span>samplePlace;//抽样地点</span> \n </li>\n <li>\n  <span>storeTemperature;//仓储温度</span> \n </li>\n <li>\n  <span>storeHnmidity;//仓储湿度</span> \n </li>\n</ul>\n<p>\n packageCondition;//包装情况\n</p>\n<ul>\n <li>\n  <span>outPackage;//外包装</span> \n </li>\n <li>\n  <span>sealing;//封固</span> \n </li>\n <li>\n  <span>outPackageMaterial;//外包材料</span> \n </li>\n <li>\n  <span>inPackageMaterial;//内包材料</span> \n </li>\n</ul>\n<span>labelCheck;//标签核对</span> \n<ul>\n <li>\n  <span>pharmaceuticalName;//品名</span> \n </li>\n <li>\n  <span>specification;//规格</span> \n </li>\n <li>\n  <span>packageGuiGe;//包装规格</span> \n </li>\n <li>\n  <span>validityPeriod;//有效期</span> \n </li>\n <li>\n  <span>productDepartment;//生产厂商</span> \n </li>\n <li>\n  <span>registerNumber;//注册证号</span> \n </li>\n <li>\n  <span>lotNumber;//批号</span> \n </li>\n <li>\n  <span>number;//数量</span> \n </li>\n <li>\n  <span>unitsNumber;//件数</span> \n </li>\n <li>\n  <span>others;//其他</span> \n </li>\n</ul>\n<span>sampleCondition;//抽样情况</span> \n<ul>\n <li>\n  <span>checkNumber;//报验数量</span> \n </li>\n <li>\n  <span>sampleUnitsNumber;//抽样件数</span> \n </li>\n <li>\n  <span>sampleNumber;//抽样数量</span> \n </li>\n <li>\n  <span>sampleUnit;//样品单位</span> \n </li>\n <li>\n  <span>sampleIncludeMaterial;//样品内包材</span> \n </li>\n <li>\n  <span>sampleConditionList;//存储表格数据的集合</span> \n </li>\n <ul>\n  <li>\n   <span>xuhao;//序号</span> \n  </li>\n  <li>\n   <span>xianghao;//箱号</span> \n  </li>\n  <li>\n   <span>one;//第1份</span> \n  </li>\n  <li>\n   <span>two;//第2份</span> \n  </li>\n  <li>\n   <span>three;//第3份</span> \n  </li>\n </ul>\n</ul>\n<p>\n checkDepartment;//报验单位\n</p>\n<ul>\n <li>\n  <span>checkDepartment;//报验单位</span> \n </li>\n <li>\n  <span>checkDepartmentHandler;//报验单位经手人</span> \n </li>\n <li>\n  <span>checkDepartmentPhone;//报验单位电话</span> \n </li>\n</ul>\n<p>\n sampleDepartment;//抽样单位\n</p>\n<ul>\n <li>\n  <span>sampleDepartment;//抽样单位</span> \n </li>\n <li>\n  <span>sampleDepartmentPhone;//抽样单位电话</span> \n </li>\n <li>\n  <span>sampleDepartmentHandler;//抽样单位经手人</span> \n </li>\n <li>\n  <span>sampleDate;//抽样日期</span> \n </li>\n <li>\n  <span>sampleDepartmentList;//存储表格数据的集合</span> \n </li>\n <ul>\n  <li>\n   <span>xuhao;//序号</span> \n  </li>\n  <li>\n   <span>jingshouren;//经手人</span> \n  </li>\n  <li>\n   <span>mima;//密码</span> \n  </li>\n </ul>\n</ul>', NULL, '', 0, 168, 26),
(170, '表说明', 0, 0, 0, '<p>\n API管理相关\n</p>\n<ul>\n <li>\n  dy_man_userapi\n </li>\n <li>\n  dy_man_user_model\n </li>\n</ul>\n系统相关\n<ul>\n <li>\n  dy_user [用户表]<br />\n </li>\n <li>\n  dy_log [日志]<br />\n </li>\n</ul>\n<p>\n 抽验单\n</p>\n<ul>\n <li>\n  dy_inspectionunit [报验单位]<br />\n </li>\n <li>\n  dy_lablecheck [核对标签]<br />\n </li>\n <li>\n  dy_registration [主记录]<br />\n </li>\n <li>\n  dy_registration_descr [主记录的附加记录]<br />\n </li>\n <li>\n  dy_registration_more [更多的附加记录]<br />\n </li>\n <li>\n  dy_samplingunit  [抽样单位]<br />\n </li>\n <li>\n  dy_typeoddid [检验单号]<br />\n </li>\n</ul>\n<p>\n 字典\n</p>\n<ul>\n <li>\n  dy_type[类型 现在只用了药品检验单]\n </li>\n</ul>', NULL, '', 0, 190, 24),
