(186, '应用端', 0, 0, 0, '<p>\n 改写构造函数\n</p>\n<p>\n <br />\n</p>\n<pre class="brush:php"> function __construct()\n {\n    parent::__construct();\n    $this->S= new Set();  //加载通用set类\n    $this->S->singleton(''Db'', function ($c) { //db回调\n        return new Db();\n    });\n }</pre>\n<p>\n <br />\n</p>', NULL, '', 0, 184, 0),
(187, '接口应用端', 0, 0, 0, '<p>\n 基础方法\n</p>\n<p>\n construct\n</p>\n<pre class="brush:php">    public function __construct($params)    //$params 是路由参数\n    {\n        $this->CI =& get_instance();\n        $this->params = $params;\n        $this->tmp[''timestamp_''] = $this->CI->T();\n        //$sign //参数是签名\n    }</pre>\n<p>\n 接口输出方法\n</p>\n<p>\n <br />\n</p>\n<pre class="brush:php">    private function data($data)\n    {\n        $this->data[''data''] = $data;\n    }\n    private function data2($data)\n    {\n        $this->data[''data2''] = $data;\n    }\n\n    private function msg($msg = '''')\n    {\n        $this->data[''msg''] = $msg;\n    }\n    private function code($code = '''')\n    {\n        $code = intval($code);\n        $this->data[''code''] = $code;\n    }\n    public function D($code = 0, $data)\n    {\n        $code = intval($code);\n        if (!empty($code)) $this->data[''code''] = $code;\n        $this->data[''data''] = $data;\n        $this->data[''timestamp_''] = $this->tmp[''timestamp_''];\n        $this->data[''timestamp''] = $this->CI->T();\n        $this->data[''debugpath''] = ''Enter'';\n        echo json_encode($this->data);\n        exit;\n    }\n    public function J($code=0,$msg='''')\n    {\n        $code = intval($code);\n        if(!empty($code))  $this->data[''code''] = $code;\n        if(!empty($msg))   $this->data[''msg'']  = $msg;\n        $this->data[''timestamp_''] = $this->tmp[''timestamp_''];\n        $this->data[''timestamp'']  = $this->CI->T();\n        $this->data[''debugpath'']  = ''Enter'';\n        echo json_encode($this->data);\n        exit;\n    }\n</pre>\n<p>\n <br />\n</p>\n<p>\n 魔术方法\n</p>\n<pre class="brush:php">    //引用重定向到ci->s下\n    public function __get($key)\n    {\n        return $this->CI->S->$key;\n    }\n    public function __set($key, $value)\n    {\n        return $this->CI->S->$key = $value;\n    }\n    public function __isset($key)\n    {\n        return isset($this->CI->S->$key);\n    }\n    public function __unset($key)\n    {\n        unset($this->CI->S->$key);\n    }</pre>\n<p>\n <br />\n</p>', NULL, '', 0, 184, 0),
