(202, '基础函数', 0, 0, 0, '<pre class="brush:php">serialize();   //序列化\nunserialize(); //反序列化\narray_merge($keys,$task); //数组合并\narray_unique($result);    //排重\narray_diff($task,$tasko); //想减\nimplode('','',$arr);\nexplode('','',$string);\nshuffle($rc);  //打乱数组\narray_flip()   //键值反转\n$arr = !empty($urllist)?array_unique($urllist):array();//排重 </pre>', NULL, '', 0, 200, 14),
(203, '数组函数', 0, 0, 0, '<pre class="brush:php">//熟悉和了解\narray_diff()   //返回两个数组的差集数组。\narray_unique() //删除数组中重复的值。\narray_count_values() //用于统计数组中所有值出现的次数。\narray_intersect() //计算数组的交集。\narray_rand()   //从数组中随机选出一个或多个元素，并返回。\nin_array()     //检查数组中是否存在指定的值。\narray_search() //在数组中搜索给定的值，如果成功则返回相应的键名。\narsort() //对数组进行逆向排序并保持索引关系。\nasort()  //对数组进行排序并保持索引关系。\nkrsort() //对数组按照键名逆向排序。\nksort()  //对数组按照键名排序。\nrsort()  //对数组逆向排序。\nsort()   //对数组排序。\n\narray()            //创建数组。\narray_change_key_case() //返回其键均为大写或小写的数组。\narray_chunk()      //把一个数组分割为新的数组块。\narray_combine()    //通过合并两个数组来创建一个新数组。\narray_diff_assoc() //比较键名和键值，并返回两个数组的差集数组。\narray_diff_key()   //比较键名，并返回两个数组的差集数组。\narray_diff_uassoc()//通过用户提供的回调函数做索引检查来计算数组的差集。\narray_diff_ukey()  //用回调函数对键名比较计算数组的差集。\narray_fill()       //用给定的值填充数组。\narray_filter()     //用回调函数过滤数组中的元素。\narray_flip()       //交换数组中的键和值。\narray_intersect_assoc() //比较键名和键值，并返回两个数组的交集数组。\narray_intersect_key()   //使用键名比较计算数组的交集。\narray_intersect_uassoc()//带索引检查计算数组的交集，用回调函数比较索引。\narray_intersect_ukey()  //用回调函数比较键名来计算数组的交集。\narray_key_exists()      //检查给定的键名或索引是否存在于数组中。\narray_keys()  //返回数组中所有的键名。\narray_map()   //将回调函数作用到给定数组的单元上。\narray_merge() //把一个或多个数组合并为一个数组。\narray_merge_recursive() //递归地合并一个或多个数组。\narray_multisort() //对多个数组或多维数组进行排序。\narray_pad()      //用值将数组填补到指定长度。\narray_pop()      //将数组最后一个单元弹出（出栈）。\narray_product()  //计算数组中所有值的乘积。\narray_push()     //将一个或多个单元（元素）压入数组的末尾（入栈）。\narray_reduce()   //用回调函数迭代地将数组简化为单一的值。\narray_reverse()  //将原数组中的元素顺序翻转，创建新的数组并返回。\narray_shift()    //删除数组中的第一个元素，并返回被删除元素的值。\narray_slice()    //在数组中根据条件取出一段值，并返回。\narray_splice()   //把数组中的一部分去掉并用其它值取代。\narray_sum()      //计算数组中所有值的和。\narray_udiff()    //用回调函数比较数据来计算数组的差集。\narray_udiff_assoc() //带索引检查计算数组的差集，用回调函数比较数据。\narray_udiff_uassoc()//带索引检查计算数组的差集，用回调函数比较数据和索引。\narray_uintersect()  //计算数组的交集，用回调函数比较数据。\narray_uintersect_assoc() //带索引检查计算数组的交集，用回调函数比较数据。\narray_uintersect_uassoc()//带索引检查计算数组的交集，用回调函数比较数据和索引。\narray_unshift() //在数组开头插入一个或多个元素。\narray_values()  //返回数组中所有的值。\narray_walk()    //对数组中的每个成员应用用户函数。\narray_walk_recursive() //对数组中的每个成员递归地应用用户函数。\n\n//其他\ncompact() //建立一个数组，包括变量名和它们的值。\ncount()   //计算数组中的元素数目或对象中的属性个数。\ncurrent() //返回数组中的当前元素。\neach()    //返回数组中当前的键／值对并将数组指针向前移动一步。\nend()     //将数组的内部指针指向最后一个元素。\nextract() //从数组中将变量导入到当前的符号表。\nkey()     //从关联数组中取得键名。\nlist()    //把数组中的值赋给一些变量。\nnatcasesort() //用“自然排序”算法对数组进行不区分大小写字母的排序。\nnatsort() //用“自然排序”算法对数组排序。\nnext()    //将数组中的内部指针向前移动一位。\npos()     //current()的别名。\nprev()    //将数组的内部指针倒回一位。\nrange()   //建立一个包含指定范围的元素的数组。\nreset()   //将数组的内部指针指向第一个元素。\nshuffle() //把数组中的元素按随机顺序重新排列。\nsizeof()  //count() 的别名。\nuasort()  //使用用户自定义的比较函数对数组中的值进行排序并保持索引关联。\nuksort()  // 使用用户自定义的比较函数对数组中的键名进行排序。\nusort()   //使用用户自定义的比较函数对数组中的值进行排序\n</pre>\n<br />', NULL, '', 0, 200, 10),
