(259, '分层体系概述', 0, 0, 0, '所有的知识体系都在其中<br />', NULL, '', 0, 197, 200),
(260, '后端', 0, 0, 0, '<p>\n 支撑点 : 数据库[Mysql / Mongodb]\n</p>\n<p>\n 支撑点 : NG apache 等发布工具\n</p>\n<p>\n 等等服务器软件支撑\n</p>', NULL, '', 0, 259, 8),
(261, '前端', 0, 0, 0, '<p>\n 支撑点 : 页面\n</p>\n<ul>\n <li>\n  页面输出<br />\n </li>\n <li>\n  data输出\n </li>\n <li>\n  json输出\n </li>\n <li>\n  xml输出\n </li>\n</ul>', NULL, '', 0, 259, 14),
(262, '中间逻辑', 0, 0, 0, '<p>\n 支撑点 : cms\n</p>\n<p>\n mvc架构\n</p>', NULL, '', 0, 259, 12),
(263, '再后端', 0, 0, 0, '支撑点 : 服务器架构', NULL, '', 0, 259, 6),
(264, '大前端', 0, 0, 0, '<p>\n 支撑点 : 前端框架\n</p>\n<p>\n Bootstrap / Jquery / 各种插件组件\n</p>', NULL, '', 0, 259, 16),
(265, '中间缓存', 0, 0, 0, '<p>\n 支撑点 : 缓存体系\n</p>\n<p>\n 支撑点 : [APC / Memcache / 文件缓存 / 页面缓存 等等]\n</p>', NULL, '', 0, 259, 10),
(266, '前端特效', 0, 0, 0, '在框架基础之上开发系列特殊效果,超越框架', NULL, '', 0, 259, 18),
(267, '大后端', 0, 0, 0, '<p>\n 理论体系\n</p>\n<p>\n 对上面诸层进行理论支撑\n</p>\n<p>\n 包括各类理论图表 规划 等等\n</p>', NULL, '', 0, 259, 4),
(268, '中间日志', 0, 0, 0, '支撑点 : 专门的日志系统和日志服务器可以很方便的进行读取和读写<br />', NULL, '', 0, 259, 11),
(269, '概念一', 0, 1, 0, '概念一', '123', '123', 0, 239, 0),
(270, '概念二', 0, 1, 0, '概念二1', 'ceshi yibncang<br />', '', 0, 239, 0);
